/// \file
/// This file declares the targeting of the RegisterBankInfo class for RISCV64.
/// \todo This should be generated by TableGen.
//===----------------------------------------------------------------------===//

#ifndef LLVM_LIB_TARGET_MIPS_MIPSREGISTERBANKINFO_H
#define LLVM_LIB_TARGET_MIPS_MIPSREGISTERBANKINFO_H

#include "llvm/CodeGen/GlobalISel/RegisterBankInfo.h"

#define GET_REGBANK_DECLARATIONS
#include "RISCV64GenRegisterBank.inc"

namespace llvm {

class TargetRegisterInfo;

class RISCV64GenRegisterBankInfo : public RegisterBankInfo {
#define GET_TARGET_REGBANK_CLASS
#include "RISCV64GenRegisterBank.inc"
};

/// This class provides the information for the target register banks.
class RISCV64RegisterBankInfo final : public RISCV64GenRegisterBankInfo {
public:
  RISCV64RegisterBankInfo(const TargetRegisterInfo &TRI);

  const RegisterBank &
  getRegBankFromRegClass(const TargetRegisterClass &RC) const override;

  const InstructionMapping &
  getInstrMapping(const MachineInstr &MI) const override;
};
} // end namespace llvm
#endif
